// src/components/FileUpload.jsx
import React, { useState } from "react";

export default function FileUpload({ threadId }) {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  async function onUpload() {
    if (!file) return;
    setUploading(true);

    const reader = new FileReader();
    reader.onload = async () => {
      try {
        const base64Data = reader.result.split(",")[1]; // remove `data:*/*;base64,`
        const res = await fetch("/api/uploadToVectorStore", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            fileName: file.name,
            fileType: file.type,
            base64Data,
            threadId,
          }),
        });

        if (!res.ok) throw new Error("Upload failed: " + res.statusText);

        const result = await res.json();
        alert("✅ File uploaded with ID: " + result.fileId);
      } catch (err) {
        console.error(err);
        alert("Upload failed: " + err.message);
      } finally {
        setUploading(false);
      }
    };

    reader.readAsDataURL(file); // triggers onload
  }

  return (
    <div style={{ marginTop: 20 }}>
      <h3>Upload a Document</h3>
      <input
        type="file"
        onChange={(e) => setFile(e.target.files[0])}
        style={{ marginRight: 10 }}
      />
      <button onClick={onUpload} disabled={!file || uploading}>
        {uploading ? "Uploading..." : "Upload"}
      </button>
    </div>
  );
}
