// src/utils/export.js
import jsPDF from "jspdf";

export const exportToPdf = (messages) => {
  const doc = new jsPDF();

  let y = 10;

  messages.forEach((msg, index) => {
    const prefix = msg.role === "user" ? "You: " : msg.role === "assistant" ? "Assistant: " : "System: ";
    const text = prefix + msg.text;

    // Split long lines to fit the page
    const lines = doc.splitTextToSize(text, 180);
    doc.text(lines, 10, y);

    y += lines.length * 10;

    // Add new page if space runs out
    if (y > 280) {
      doc.addPage();
      y = 10;
    }
  });

  doc.save("chat-export.pdf");
};

export const exportToDocx = (messages) => {
  alert("DOCX Export triggered!\n" + JSON.stringify(messages, null, 2));
};
