// frontend/components/FileUpload.jsx
import React, { useState } from "react";

export default function FileUpload({ onFileUploaded }) {
  const [uploading, setUploading] = useState(false);
  const [file, setFile] = useState(null);

  const handleUpload = async () => {
    if (!file) return;

    setUploading(true);

    const reader = new FileReader();
    reader.onload = async () => {
      try {
        const base64Data = reader.result.split(",")[1]; // Strip data:*/*;base64,
        const res = await fetch("/api/uploadFile", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            fileName: file.name,
            fileType: file.type,
            base64Data,
          }),
        });

        const result = await res.json();

        if (!res.ok) throw new Error(result.error || "Upload failed");

        // Pass fileId to parent (e.g., chat input component)
        onFileUploaded(result.fileId);
      } catch (err) {
        console.error(err);
        alert("Upload error: " + err.message);
      } finally {
        setUploading(false);
        setFile(null);
      }
    };

    reader.readAsDataURL(file);
  };

  return (
    <div style={{ marginTop: 20 }}>
      <input
        type="file"
        onChange={(e) => setFile(e.target.files[0])}
        disabled={uploading}
      />
      <button onClick={handleUpload} disabled={!file || uploading}>
        {uploading ? "Uploading..." : "Upload"}
      </button>
    </div>
  );
}
